//media queries
@use "./variables";
@use "./functions";
@use 'sass:color';

@mixin mQ($arg) {
    @media screen and (min-width: $arg) {
        @content;
    }
}

//main button
@mixin mainBtn {   
    font-size: functions.calculateRem(16);
    font-weight: 900;
    line-height: 26px;
    color: variables.$clr-white;
    background-color: variables.$clr-turquoise;
    border: 2px solid transparent;
    padding: 14px 36px;
    border-radius: 29px;
    text-decoration: none;
    text-align: center;
    display: block;

    &:hover,
    &:focus {
        cursor: pointer;
        background-color: color.scale(variables.$clr-turquoise, $lightness: 20%);
    }
}

@mixin mainSpan {
    width: 56px;
    height: 56px;
    border: 1px solid variables.$clr-grey-purple;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 50%;
    font-size: functions.calculateRem(16);
    font-weight: 900;
    margin: auto;
    position: relative;

    &:before {
        content: "";
        width: 1px;
        height: 84px;
        background-color: variables.$clr-grey-purple;
        position: absolute;
        top: -85px;
        left: 50%;
    }
}

